<?php$contact=array(	'name'=>$data['name'],	'linked_leads_id' => array(		$lead_id	),	'responsible_user_id' => $dm_id,	'tags' => $tag_id, #Теги	'custom_fields'=>array(		array(			'id'=>$custom_fields['EMAIL'],			'values'=>array(				array(					'value'=>$data['email'],					'enum'=>'WORK'				)			)		)	));if(!empty($data['firstname']))	$contact['custom_fields'][]=array(		'id'=>'1480011',		'values'=>array(			array(				'value'=>$data['firstname']			)		));if(!empty($data['lastname']))	$contact['custom_fields'][]=array(		'id'=>'1480013',		'values'=>array(			array(				'value'=>$data['lastname']			)		));if(!empty($data['phone']))	$contact['custom_fields'][]=array(		'id'=>$custom_fields['PHONE'],		'values'=>array(			array(				'value'=>$data['phone'],				'enum'=>'OTHER'			)		));if(!empty($data['city']))	$contact['custom_fields'][]=array(		'id' => '1477807',		'values'=>array(			array(				'value'=>$data['city'],				'subtype'=>'city'			)		));$set['request']['contacts']['add'][]=$contact;#Формируем ссылку для запроса$link='https://'.$subdomain.'.amocrm.ru/private/api/v2/json/contacts/set';$curl=curl_init(); #Сохраняем дескриптор сеанса cURL#Устанавливаем необходимые опции для сеанса cURLcurl_setopt($curl,CURLOPT_RETURNTRANSFER,true);curl_setopt($curl,CURLOPT_USERAGENT,'amoCRM-API-client/1.0');curl_setopt($curl,CURLOPT_URL,$link);curl_setopt($curl,CURLOPT_CUSTOMREQUEST,'POST');curl_setopt($curl,CURLOPT_POSTFIELDS,json_encode($set));curl_setopt($curl,CURLOPT_HTTPHEADER,array('Content-Type: application/json'));curl_setopt($curl,CURLOPT_HEADER,false);curl_setopt($curl,CURLOPT_COOKIEFILE,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_COOKIEJAR,dirname(__FILE__).'/cookie.txt'); #PHP>5.3.6 dirname(__FILE__) -> __DIR__curl_setopt($curl,CURLOPT_SSL_VERIFYPEER,0);curl_setopt($curl,CURLOPT_SSL_VERIFYHOST,0);$out=curl_exec($curl); #Инициируем запрос к API и сохраняем ответ в переменную$code=curl_getinfo($curl,CURLINFO_HTTP_CODE);CheckCurlResponse($code);